mapper.select:
SELECT
idx,
user_idx,
occasion,
date,
food_record
FROM calender_meal

    idx = #{idx}
    AND user_idx = #{userIdx}
    AND occasion = #{occasion}
    AND date = #{date}
    AND food_record = #{foodRecord}

mapper.update:
UPDATE calender_meal

     user_idx = #{userIdx}
     occasion = #{occasion}
    , date = #{date}
    , food_record = #{foodRecord}

WHERE IDX = #{idx}
