mapper.select:
SELECT
idx,
user_idx,
occasion,
date,
food_idx
FROM calender_recommended

    idx = #{idx}
    AND user_idx = #{userIdx}
    AND occasion = #{occasion}
    AND date = #{date}
    AND food_idx = #{foodIdx}

mapper.update:
UPDATE calender_recommended

     user_idx = #{userIdx}
     occasion = #{occasion}
    , date = #{date}
    , food_idx = #{foodIdx}

WHERE IDX = #{idx}

mapper.select:
SELECT
f.name,
f.idx AS food_idx
FROM calender_recommended AS cr
     LEFT OUTER JOIN food AS f
     ON cr.food_idx = f.idx

    cr.user_idx = #{userIdx}
    AND cr.date = #{date}
    AND cr.occasion = #{occasion}
