mapper.select:
SELECT
idx,
kcal,
carbo,
protein,
fat,
salt,
chole,
food_idx
FROM food_nutri

    idx = #{idx}
    AND kcal = #{kcal}
    AND carbo = #{carbo}
    AND protein = #{protein}
    AND fat = #{fat}
    AND salt = #{salt}
    AND chole = #{chole}
    AND food_idx = #{foodIdx}

mapper.update:
UPDATE food_nutri

     kcal = #{kcal}
    , carbo = #{carbo}
    , protein = #{protein}
    , fat = #{fat}
    , salt = #{salt}
    , chole = #{chole}
    , food_idx = #{foodIdx}

WHERE IDX = #{idx}
